export default class WorkTracker {
    constructor(root){
        this.root = root;
        this.root.insertAdjacentHTML("afterbegin", WorkTracker.html());

        this.loadEntries();
        this.updateView();
    }

    static html() {
        return `
        <table class="tracker">
            <thead>
                <tr>
                    <th>Date</th>
                    <th>Workout</th>
                    <th>Duration</th>
                    <th></th>
                </tr>
            </thead>
            <tbody class="tracker__entries"></tbody>
            <tbody>
                <tr class="tracker__row tracker__row--add">
                    <td colspan="4">
                        <span class="tracker__add">Add Entry &plus;</span>

                    </td>
                </tr>
            </tbody>
        </table>
        `;
    }

    loadEntries() {
        this.entries = JSON.parse(localStorage.getItem("workout-tracker-entries") || "[]");
    }

    saveEntries() {
        localStorage.setItem("workout-tracker-entries", JSON.stringify(this.entries));
    }

    updateView() {
        
    }
}
